import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux'
import 'boxicons'
import MaterialTable from 'material-table'
import './Batches.css'
const AdminBatches = () => {
    const { val } = useSelector((state) => state.toggle);
    const [year, setYear] = useState(null);
    const [batch, setBatch] = useState(null);
    const[tableData,setTableData] = useState([])
    const [flag, setFlag] = useState(true);
    const years = [{ id: "1", yr: "First" }, { id: "2", yr: "Second" }, { id: "3", yr: "Third" }, { id: "4", yr: "Fourth" }];
    const batches = [{ id: "1", batch: "CSE" }, { id: "2", batch: "IT" }, { id: "3", batch: "ECE" }, { id: "4", batch: "EN" }];
    const handleYearDropdown = (e) => {
        setYear(e.target.value);
        setFlag(false);
    }
    const handleBatchDropdown = (e) => {
        setBatch(e.target.value);
    }
    if (year) {
        console.log(year);
    }
    if (batch) {
        console.log(batch);
    }
    const columns = [{
        title: "Roll number",
        field: "Roll number",
        cellStyle: {
            paddingRight: "15%",
            border: "0px solid transparent",
            overflowX: "hidden",
        },
        headerStyle: {
            width: 20,
            maxWidth: 20,
            paddingRight: "15%",
            color: " #1E637E",
            fontFamily: "Inter",
            fontWeight: "500",
            fontSize: "16px",
        },
        align: "center",
    },
    {
        title: "Name",
        field: "Name",
        cellStyle: {
            paddingRight: "22%",
            border: "0px solid transparent",
            overflowX: "hidden",
        },
        headerStyle: {
            width: 10,
            maxWidth: 10,
            paddingRight: "22%",
            color: " #1E637E",
            fontFamily: "Inter",
            fontWeight: "500",
            fontSize: "16px",
        }, align: "center"
    },
    {
        title: "Email",
        field: "description",
        cellStyle: {
            border: "0px solid transparent",
            overflowX: "hidden"
        },
        headerStyle: {
            width: 10,
            maxWidth: 10,
            color: " #1E637E",
            fontFamily: "Inter",
            fontWeight: "500",
            fontSize: "16px",

        },
        align: "center"
    }];
    const style = {
        width: "90vw", minHeight: "90vh", overflow:"revert",scrollbars:"hidden",
    }
    const activateStyle = {
        width: "81vw", minHeight: "90vh",
    }
    return (
        <div className={`Admin-Container ${val ? "activate" : ""}`}>
            <div className='Batches-Details'>
                <div className='Year-Dropdown'>
                    <select id="year-drop" onChange={handleYearDropdown}>
                        <option selected="selected" hidden>Select Year</option>
                        {
                            years.map((y) => (
                                <option key={y.id} name={y.id} value={y.yr}>{y.yr}</option>
                            ))
                        }
                    </select>
                </div>
                <div className='Batch-Dropdown'>
                    <select id="batch-drop" disabled={flag} onChange={handleBatchDropdown}>
                        <option selected="selected" hidden>Select Batch</option>
                        {
                            batches.map((b) => (
                                <option key={b.id} name={b.id} value={b.batch}>{b.batch}</option>
                            ))
                        }
                    </select>
                </div>
                <div className='Apply-Button'>
                    <div className='Apply-text'>
                        <h4>Apply</h4>
                    </div>
                </div>
                <div className='Add-Batch'>
                    <div className='Batch-text'>
                        <h4>Add Batch</h4>
                    </div>
                </div>
            </div>
            <div className='Add-Student'>
                <div className='plus-icon'>
                    <box-icon name='plus-circle' color="#007BAB" ></box-icon>
                </div>
                <div className='Add-Student-text'>
                    <h4>Add student</h4>
                </div>
            </div>
            <div className='Student-Info-Table'>
                <MaterialTable style={val ? activateStyle : style} columns={columns} data={tableData} title={""} options={{ search: false, sorting: false, toolbar: false, maxBodyHeight: 400, tableLayout: "100%", paging: false, draggable: false, actionsColumnIndex: -1 }} actions={[{
                    icon: () => <box-icon color="red" name='trash-alt' />,
                    tooltip: "Delete annoucement",
                    onClick: (e, ide) => {
                        console.log(ide._id)
                    }
                }]} localization={{
                    header: {
                        actions: ""
                    }
                }} />

            </div>
        </div>);
};

export default AdminBatches;
