import React, { useState, useEffect } from 'react'
import SubmitButton from '../../../Components/UI/Button/Button'
import { useForm } from 'react-hook-form'
import * as actionCreators from "../../../Service/Action/action";
import { useDispatch, useSelector } from 'react-redux'
import Spinner from '../../../Components/UI/Spinner/Spinner';
const AddBatch = (props) => {

    const { register, handleSubmit, formState: { errors }, reset } = useForm({
        mode: "onTouched",
    });
    const onSubmit = (data, e) => {
        e.preventDefault();

        reset();
    }
    const handleClick = (e) => {
        e.preventDefault();
        props.setTrigger(false);
    }
    const years = [{ id: "1", yr: "First" }, { id: "2", yr: "Second" }, { id: "3", yr: "Third" }, { id: "4", yr: "Fourth" }];

    return   (
        <div className='Modal-box'>
            <div className='Batch-Form'>
                <div className='Batch-Form-Heading'>
                    <h2>Batch Details</h2>
                </div>
                <div>
                    <form className='Batch-Input' onSubmit={handleSubmit(onSubmit)}>
                        <div className='combine-input'>
                            <div className='Label-form'>
                                <label htmlFor="Full-Name">
                                    Batch Name
                                </label>
                            </div>
                            <div className='Input-detail'>
                                <input className='input-field-form' size={"40"} type="text" name="batch" {...register("batch", { required: "**batch is required", })}></input>
                            </div>
                            <p className='alerts'>{errors.batch?.message}</p>
                        </div>
                        <div className='Year-Dropdown'>
                            <select id="year-drop-batch" onChange={handleYearDropdown}>
                                <option selected="selected" hidden>Select Year</option>
                                {
                                    years.map((y) => (
                                        <option key={y.id} name={y.id} value={y.yr}>{y.yr}</option>
                                    ))
                                }
                            </select>
                        </div>
                        <div className='combine-input'>
                            <div className='Label-form'>
                                <label htmlFor="Semester">
                                    Semester
                                </label>
                            </div>
                            <div className='Input-detail'>
                                <input className='input-field-form' size={"40"} type="text" name="Semester" {...register("Semester", { required: "**Semester is required", })}></input>
                            </div>
                            <p className='alerts'>{errors.Semester?.message}</p>
                        </div>
                        <div className='Button-update'>
                            <SubmitButton className="Admin-Details-Update" Label="Update" ></SubmitButton>
                        </div>
                    </form>
                </div>
                <div className='cross1'>
                    <i id="crossed" className="fa fa-times" onClick={handleClick} ></i>
                </div>
            </div>
        </div>
    );
}

export default AddBatch
