import React, { useState } from 'react';
import './Marks.css'
import '../../Admin/Batches/Batches.css'
import { useDispatch, useSelector } from 'react-redux'
import { useForm } from 'react-hook-form'
const GiveMarks = () => {
  const { val } = useSelector((state) => state.toggle);
  const [batch, setBatch] = useState(null);
  const [batches , setBatches] = useState([]);
  const [flag, setFlag] = useState(true);
  const [sub, setSub] = useState(null);
  const [year, setYear] = useState(null);
  const years = [{ id: "1", yr: "First" }, { id: "2", yr: "Second" }, { id: "3", yr: "Third" }, { id: "4", yr: "Fourth" }];
  const [subject, setSubject] = useState([{ id: "1", sub: "maths" },
  { id: "2", sub: "maths" },
  { id: "3", sub: "maths" },
  { id: "4", sub: "maths" },])
  const { register, handleSubmit, formState: { errors }, reset } = useForm({
    mode: "onTouched"
});
  const handleSubDropdown = (e) => {
    setSub(e.target.value);
    console.log(e.target.value);
    setFlag(false);
  }
  const handleYearDropdown = (e) => {
    setYear(e.target.value);
    console.log(e.target.value);
    setFlag(false);
}
  const handleBatchDropdown = (e) => {
    setBatch(e.target.value);
  }
  return (<>
    <div className={`Admin-Container ${val ? "activate" : ""}`} >
      <div className='Batches-Detail'>
        <div className='Year-Dropdown'>
          <select id="year-drop" onChange={handleYearDropdown}>
            <option selected="selected" hidden>Select Year</option>
            {
              years.map((y) => (
                <option key={y.id} name={y.id} value={y.id}>{y.yr}</option>
              ))
            }
          </select>
        </div>
        <div className='Subject-Dropdown'>
          <select id="sub-drop" onChange={handleSubDropdown}>
            <option selected="selected" hidden>Select Subject</option>
            {
              subject.map((y) => (
                <option key={y.id} name={y.id} value={y.sub}>{y.sub}</option>
              ))
            }
          </select>
        </div>
        <div className='Batch-Dropdown'>
          <select id="batch-drop" onChange={handleBatchDropdown}>
            <option selected="selected" hidden>Select Batch</option>
            {
              batches.map((b) => (
                <option key={b._id} name={b._id} value={b._id}>{b._id}</option>
              ))
            }
          </select>
        </div>
      </div>
      <div className='annoucement-opt'>
                            <div className='faculty-radio'>
                                <label className='label-data' htmlFor="field-Faculty">
                                    <input
                                        {...register("aopt", { required: "**This field is required" })}
                                        type="radio"
                                        name="aopt"
                                        value="Faculty"
                                        id="field-Faculty"
                                    />
                                    Faculty
                                </label>

                            </div>
                            <div className='student-radio'>
                            <label className='label-data' htmlFor="field-Student">
                                    <input
                                        {...register("aopt", { required: "**This field is required" })}
                                        type="radio"
                                        name="aopt"
                                        value="Student"
                                        id="field-Student"
                                    />
                                    Student
                                </label>
                            </div>
                            <div className='both-radio'>
                            <label className='label-data' htmlFor="field-Both">
                                    <input
                                        {...register("aopt", { required: "**This field is required" })}
                                        type="radio"
                                        name="aopt"
                                        value="Both"
                                        id="field-Both"
                                    />
                                    Both
                                </label>
                            </div>
                        </div>
                        <p className='alerts'>{errors.aopt?.message}</p>
    </div>
  </>);
};

export default GiveMarks;
