import React from 'react';
import Chart from "react-apexcharts";
const DistributedBar = () => {
    const option = {
        series: [{
        data: [21, 22, 10, 28, 16, 21, 13, 30],
       
      }],
        chart: {
        height: 350,
        width: "100%" ,
        type: 'bar',
        stacked: true,
        toolbar: {
            show: false
          },
      },
      colors: ["#FF902A","#FF902A","#FF902A","#FF902A","#FF902A","#FF902A","#FF902A","#FF902A"],
      plotOptions: {
        bar: {
          columnWidth: '29%',
          distributed: true,
          background:"#000fff"
        },
      },
      dataLabels: {
        enabled: true,
        offSetY:1
      },
      legend: {
        show: false
      },
      xaxis: {
        axisBorder: { show: true },
        axisTicks: { show: false },
        categories: [
            "maths",
            "physics",
            "chemistry",
            "biology",
            "reactjs",
            "Nodejs",
            "spring",
           "django", 
        ],
        labels: {
          style: {
            colors: ["#000000","#FF902A","#FF902A","#FF902A","#FF902A","#FF902A","#FF902A","#FF902A"],
            fontSize: '12px'
          }
        }
      },
      yaxis: { show: false },
        grid: {
            show: false,
          },
      };
  return <div>
      <Chart options={option} series={option.series} type="bar" height={180} />
  </div>;
};

export default DistributedBar;
